mapper.update:
update sku_property
set catalog_id = #{catalogId},
  name = #{name},
  type = #{type},
  sort = #{sort}
where id = #{id}

mapper.select:
select id, catalog_id, name, type, sort
from sku_property
where id = #{id}

     select id, catalog_id, name, sort, type
from sku_property
where catalog_id = #{catalogId}
